import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import TextField from '@material-ui/core/TextField';

import Button from "../buttons/Button";

import '../detail/TaskDetails.css';

const TaskDetails = ({ tasks }) => {
    const params = useParams();
    const navigate = useNavigate();

    const task = tasks.find(task => task.id === params);

    const [taskDescription, setTaskDescription] = useState(task ? task.description : "Detalhes da tarefa");

    useEffect(() => {
        if (task) {
            setTaskDescription(task.description);
        }
    }, [task]);

    const handleBackButtonClick = () => {
        navigate("/");
    };

    const handleDescriptionChange = (e) => {
        setTaskDescription(e.target.value);
    };

    return (
        <>
        <div className="back-button-container">
            <Button onclick={handleBackButtonClick}>Voltar</Button>
        </div>
        <div className="task-details-container">
            <h2>{params.taskTitle}</h2>
            <TextField
                id="standard-multiline-static"
                label="Detail"
                multiline
                rows={4}
                value={taskDescription}
                onChange={handleDescriptionChange}
                inputProps={{ style: { color: '#eee' } }}
                InputLabelProps={{ style: { color: '#eee' } }}
            />
        </div>
        </>
    );
};

export default TaskDetails;
