import React from "react";
import { useParams } from "react-router-dom";
import { useNavigate } from 'react-router-dom';
import TextField from '@material-ui/core/TextField';

import Button from "../buttons/Button";
import setTaskDescription from '../../App';

import '../detail/TaskDetails.css';

const TaskDetails = () => {
    const params = useParams();
    const navigate = useNavigate();
    const [taskDescription, setTaskDescription] = useState("Detalhes da tarefa");

    const handleBackButtonClick = () => {
        navigate("/");
    };

    const handleDescriptionChange = (e) => {
        const newDescription = e.target.value;
        setTaskDescription(newDescription);
        handleTaskDescriptionChange(newDescription); // Passa a nova descrição para o componente pai (App)
    };

    return (
        <>
        <div className="back-button-container">
            <Button onclick={handleBackButtonClick}>Voltar</Button>
        </div>
        <div className="task-details-container">
            <h2>{params.taskTitle}</h2>
            <TextField
                id="standard-multiline-static"
                label="Detail"
                multiline
                rows={4}
                value={taskDescription}
                onChange={handleDescriptionChange}
                inputProps={{ style: { color: '#eee' } }}
                InputLabelProps={{ style: { color: '#eee' } }}
            />
        </div>
        </>
    );
};

export default TaskDetails;
